t_process_config.process_status = '0'
and t_process_job_relation.job_basic_id = #{jobBasicId}
and t_process_config.run_status = #{runStatus}
and t_process_job_relation.job_basic_id in
#{item}

SELECT t_process_config.id, t_process_config.process_name, t_job_basic.job_name FROM t_process_config LEFT JOIN t_process_job_relation ON t_process_config.id = t_process_job_relation.process_id LEFT JOIN t_job_basic on t_job_basic.id = t_process_job_relation.job_basic_id
t_process_config.process_status = '0'
and t_process_job_relation.job_basic_id = #{jobBasicId}
and t_process_config.run_status = #{runStatus}
and t_process_job_relation.job_basic_id in
#{item}

SELECT tpc.id FROM t_process_config tpc LEFT JOIN t_process_job_relation tpjr ON tpc.id = tpjr.process_id
tpc.process_status = '0' AND tpjr.run_time <= DATE_FORMAT( now( ), '%H:%i' ) AND ( tpjr.operating_frequency = 1 OR ( tpjr.operating_frequency = 2 AND CONVERT(tpjr.run_date,SIGNED)+1 = DAYOFWEEK( now( ) ) ) OR ( tpjr.operating_frequency = 3 AND run_date = '-1' AND DAYOFMONTH( last_day( curdate( ) ) ) = DAYOFMONTH( now( ) ) ) OR ( tpjr.operating_frequency = 3 AND tpjr.run_date <> '-1' AND tpjr.run_date = DAYOFMONTH( now( ) ) ) )
and tpc.run_status in
#{item}
and tpjr.starting_Work=#{startingWork}
