mapper.sql:
 	
 		and job.job_name = #{jobName}
	
 		and pjr.process_id  = #{processId}
	
 		and job.job_type = #{jobType}
	
	
	
 		and 1 != 1

mapper.select:
SELECT job.id, pjr.level, job.job_name FROM t_process_job_relation pjr LEFT JOIN t_job_basic job ON pjr.job_basic_id = job.id where pjr.process_id = #{processId}
and pjr.job_basic_id != #{jobBasicId}
order by pjr.level asc

SELECT id FROM t_process_job_relation
1=1
and starting_work = #{startingWork,jdbcType=VARCHAR}
and process_id =#{processId}

SELECT pjr.id, job.job_name, job.job_sketch, job.job_url, job.job_type, job.job_status, job.job_number, pjr.starting_work, pjr.operating_frequency, pjr.run_date, pjr.last_job_name, pjr.process_id, pjr.level, pjr.job_basic_id FROM t_process_job_relation pjr LEFT JOIN t_job_basic job ON pjr.job_basic_id = job.id
1=1
and pjr.starting_work = #{startingWork,jdbcType=VARCHAR}
and pjr.process_id =#{processId}
order by pjr.level asc,pjr.starting_work desc

SELECT IFNULL(group_concat(level), '') levels FROM t_process_job_relation WHERE 1=1
AND job_basic_id IN
${item}
AND process_id = #{processId}
